      v-if="$data.contractType !== 'referral'"
      class="dappContractOperation-addressDiv"
    >
      <span class="dappContractOperation-addressNameSpan">
        {{ senderName }}
      </span>
      <span
        v-if="displayAddresses"
        class="dappContractOperation-addressAddressSpan"
      >
        {{ senderAddress }}
      </span>
    </div>
    <div
      v-if="$data.contractType !== 'referral'"
      class="dappContractOperation-arrow"
    >
      <!-- Display an arrow image in svg format. Filled with #198155 (medium green). -->
      <svg
        width="25"
        height="25"
        viewBox="0 0 25 25"
        fill="#198155"
        xmlns="http://www.w3.org/2000/svg"
