    </div>
    <div
      v-if="$data.contractType !== 'auth'"
      class="dappContractOperation-addressDiv"
    >
      <span class="dappContractOperation-addressNameSpan">
        {{ recipientName }}
      </span>
      <span
        v-if="displayAddresses"
        class="dappContractOperation-addressAddressSpan"
      >
        {{ recipientAddress }}
      </span>
    </div>
    <div class="dappContractOperation-amountDiv">
      <div v-if="hasContract" class="dappContractOperation-contract">
        <span class="dappContractOperation-amountContract">
          {{ $data.contract }}
        </span>
      </div>
      <div v-else class="dappContractOperation-amount">
        <span class="dappContractOperation-amountToken">
          {{ amountToken }}
        </span>
        <span class="dappContractOperation-amountTokenName">
          {{ asset.label }}
        </span>
        <div class="dappContractOperation-amountFiat">
